    private renderDescription(description: Array<DescriptionLinePart>) {
        // return description.map((l: Array<DescriptionLinePart>, i: number) => {
        //
        //     return <div key={i}> {l.map( (p: DescriptionLinePart, j: number ) => {
        //         const classNames: string = p.highlighted ?
        //             'description-line description-line--highlighted' :
        //             'description-line';
        //         return (<span key={i + j} className={classNames}>{p.text}</span>);
        //     }) } </div>;
        // });
        return (
            <p>
                {
                    description.map( (p: DescriptionLinePart, j: number ) => {
                        const classNames: string = p.highlighted ?
                    'description-line description-line--highlighted' :
                    'description-line';
                    return (<span key={j} className={classNames}>{p.text}</span>);
                    })
                }
            </p>
        );
    }

    private renderStoryImage(place: PLACES) {
        switch (place) {
            case PLACES.ANDEA: {
                return (<img src={andeaStory} alt="Andea story"/>);
            }
            case PLACES.AKAMAI: {
                return (<img src={akamaiStory} alt="Akamai story"/>);
            }

            case PLACES.NANGOK: {
                return (<img src={nangokStory} alt="Nangok Soft story"/>);
            }

            case PLACES.JLPT: {
                return (<img src={jlptStory} alt="Japanese Language Proficiency Test story"/>);
            }

            case PLACES.AGH: {
                return (<img src={aghStory} alt="AGH University of Science and Technology story"/>);
            }
        }
    }

    private getTechnologies(technologies: Array<string>) {
        const technologiesJoined = technologies.join(' / ');
        return (
            <span className="job-description__right-panel--technology">
                {technologiesJoined}
            </span>
        );
    }

